import React, { Component } from "react";
import { Modal, Button } from "antd";
import { Input } from "antd";

class Index extends Component {
  state = { visible: false, register: false };

  render() {
    return (
      <div>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-around",
            alignItems: "center",
            height: "100vh"
          }}
        >
          <div>
            <div
              style={{
                fontWeight: "bold",
                fontSize: "4vw",
                fontFamily: "BlinkMacSystemFont"
              }}
            >
              {" "}
              SleepCoin{" "}
            </div>
            <hr />
            <div
              style={{
                fontSize: "2vw",
                fontFamily: "BlinkMacSystemFont",
                textAlign: "center"
              }}
            >
              {" "}
              Rest and invest
            </div>

            <div style={{ display: "flex", justifyContent: "center" }}>
              <Button
                style={{ fontSize: "1vw" }}
                type="link"
                onClick={() => {
                  this.setState({ visible: true });
                }}
              >
                Partnerships
              </Button>
            </div>
          </div>

          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              src="https://sleepcoin.s3-us-west-1.amazonaws.com/Screen+Shot+2020-02-15+at+11.16.03+PM.png"
              style={{ width: "25vw" }}
            />
          </div>
        </div>

        <Modal
          title="Basic Modal"
          visible={this.state.visible}
          onCancel={() => {
            this.setState({ visible: false });
          }}
          footer={null}
          title="Login"
        >
          {this.state.register ? (
            <div style={{ marginTop: "1vw" }}>
              <Input placeholder="Email" style={{ marginTop: "1vw" }} />
              <Input placeholder="Password" style={{ marginTop: "1vw" }} />
              <Input
                placeholder="Password Confirm"
                style={{ marginTop: "1vw" }}
              />
            </div>
          ) : (
            <div>
              <Input placeholder="Email" style={{ marginTop: "1vw" }} />
              <Input placeholder="Password" style={{ marginTop: "1vw" }} />{" "}
            </div>
          )}
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              marginTop: "1vw",
              justifyContent: "space-between"
            }}
          >
            <div
              style={{
                display: "flex"
              }}
            >
              <Button type="link" style={{ color: "black" }}>
                {this.state.register ? "Login" : "Register"}
              </Button>
            </div>

            <div
              style={{
                display: "flex"
              }}
            >
              <Button type="primary">Submit</Button>
            </div>
          </div>
        </Modal>
      </div>
    );
  }
}

export default Index;
