import React, { Component } from "react";
import { Tag, Button } from "antd";
import { connect } from "react-redux";
import { logoutUser, getUser } from "../redux/actions";
import Router from "next/router";

class Dashboard extends Component {
  componentDidMount() {
    this.props.getUser();
  }

  render() {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
          height: "100vh",
          width: "100vw"
        }}
      >
        <div
          style={{
            fontWeight: "bold",
            fontSize: "4vw",
            fontFamily: "BlinkMacSystemFont"
          }}
        >
          Dashboard
        </div>
        <Tag color="magenta" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Address: {this.props.auth.user.address}
        </Tag>
        <Tag color="red" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Balance:{" "}
          {this.props.auth.user.dappBalance
            ? this.props.auth.user.dappBalance
            : "0"}
        </Tag>
        <Tag color="volcano" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Ether Address: {this.props.auth.user.ethAddress}
        </Tag>
        <Tag color="orange" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Ether Balance:{" "}
          {this.props.auth.user.ethBalance
            ? this.props.auth.user.ethBalance
            : "0"}
        </Tag>
        <Button
          type="link"
          style={{ fontSize: "1vw", color: "red" }}
          onClick={() =>
            this.props.logoutUser(() => {
              Router.push("/");
            })
          }
        >
          Logout
        </Button>
        <div
          style={{
            fontSize: "2vw",
            fontFamily: "BlinkMacSystemFont"
          }}
        >
          Campaigns
        </div>
        <Button type="link" style={{ fontSize: "1vw" }}>
          {" "}
          Create Campaign{" "}
        </Button>
      </div>
    );
  }
}

const mapStateToProps = state => {
  const { auth } = state;
  return { auth };
};

export default connect(
  mapStateToProps,
  { logoutUser, getUser }
)(Dashboard);
