import React, { Component } from "react";
import { Tag, Button, Input } from "antd";
import { connect } from "react-redux";
import { logoutUser, getUser } from "../redux/actions";
import axios from "axios";
import Router from "next/router";

class Dashboard extends Component {
  componentDidMount() {
    this.props.getUser();
  }

  state = {
    value: ""
  };

  render() {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
          height: "100vh",
          width: "100vw"
        }}
      >
        <div
          style={{
            fontWeight: "bold",
            fontSize: "4vw",
            fontFamily: "BlinkMacSystemFont"
          }}
        >
          Dashboard
        </div>
        <Tag color="magenta" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Address: {this.props.auth.user.address}
        </Tag>
        <Tag color="red" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Balance:{" "}
          {this.props.auth.user.dappBalance
            ? this.props.auth.user.dappBalance
            : "0"}
        </Tag>
        <Tag color="volcano" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Ether Address: {this.props.auth.user.ethAddress}
        </Tag>
        <Tag color="orange" style={{ marginTop: "1vw", fontSize: "1vw" }}>
          Ether Balance:{" "}
          {this.props.auth.user.ethBalance
            ? this.props.auth.user.ethBalance
            : "0"}
        </Tag>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: "1vw"
          }}
        >
          <Input
            placeholder="Value"
            onChange={event => {
              this.setState({ value: event.target.value });
            }}
          />
          <Button
            type="link"
            style={{ fontSize: "1vw", color: "#1890ff" }}
            onClick={async () => {
              axios.defaults.headers.common[
                "Authorization"
              ] = localStorage.getItem("jwtToken");

              await axios({
                method: "post",
                url: "localhost:5000/api/gateway/addBalance",
                data: {
                  value: web3.utils.toWei(
                    this.state.value.toString(),
                    this.state.denom
                  )
                }
              });
            }}
          >
            Add Funds
          </Button>
        </div>
        <Button
          type="link"
          style={{ fontSize: "1vw", color: "red" }}
          onClick={() =>
            this.props.logoutUser(() => {
              Router.push("/");
            })
          }
        >
          Logout
        </Button>
        <div
          style={{
            fontSize: "2vw",
            fontFamily: "BlinkMacSystemFont"
          }}
        >
          Campaigns
        </div>
        <Button type="link" style={{ fontSize: "1vw" }}>
          {" "}
          Create Campaign{" "}
        </Button>
      </div>
    );
  }
}

const mapStateToProps = state => {
  const { auth } = state;
  return { auth };
};

export default connect(
  mapStateToProps,
  { logoutUser, getUser }
)(Dashboard);
